import React, { Component } from "react";
import axios from "axios";
import "./style.css";


export default class SearchResults extends Component {
    constructor(props) {
        super(props);
        this.state = {};


        // const movies = [
        //     { id: 0, poster_src: "https://image.tmdb.org/t/p/w1280/qcr9bBY6MVeLzriKCmJOv1562uY.jpg", title: "movie title1", summary: "movie1 overview" },
        //     { id: 1, poster_src: "https://image.tmdb.org/t/p/w1280/7LUswjn6jgLxdJ4e6sgwkULXsZI.jpg", title: "movie title2", summary: "movie2 overview" }
        // ]

        // const movieRows = [];

        // movies.forEach((movie => {

        //     const movieRow =
        //         <div className="row" key={movie.id}>
        //             <div className="col-md-3">
        //                 <img alt="poster" className="img-fluid movie-poster" src={movie.poster_src} />
        //             </div>

        //             <div className="col-md-9">
        //                 <h3>{movie.title}</h3>
        //                 <p>{movie.summary}</p>
        //             </div>
        //         </div>  

        //     movieRows.push(movieRow)
        // }))

        // this.state = { rows: movieRows }



    }

    componentDidMount() {
        const urlString = "https://api.themoviedb.org/3/search/movie?query=marvel&api_key=ab0daf48e5c9f6b2513a909b48005ede&language=en-US&page=1&include_adult=false";

        axios.get(urlString)
            .then(function (response) {
                // handle success
                console.log("fetched data successfully");
                //console.log(response);

                const results = response.data.results;
                //console.log(results[0])

                let movieRows = []; 

                results.forEach((movie) => {
                    const movieRow =
                    <div className="row" key={movie.id}>
                        <div className="col-md-3">
                            <img alt="poster" className="img-fluid movie-poster" src={movie.poster_src} />
                        </div>

                        <div className="col-md-9">
                            <h3>{movie.title}</h3>
                            <p>{movie.summary}</p>
                        </div>
                    </div>  

                movieRows.push(movieRow)
                })

                this.setState({rows: movieRows})
            })
            .catch(function (error) {
                // handle error
                console.log("fetched data UNsuccessfully");
                console.log(error);
            })
            .finally(function () {
                // always executed
            });

    }

    render() {
        return (
            <div>
                {this.state.rows}
            </div>
        );
    }

}
