                    <div className="col-md-3">
                        <img alt="poster" className="img-fluid movie-poster" src={movie.poster_src} />
                    </div>

                    <div className="col-md-9">
                        <h3>{movie.title}</h3>
                        <p>{movie.summary}</p>
                    </div>
                </div>  

            movieRows.push(movieRow)
        }))

        // this.state = { rows: movieRows }



    }

    componentDidMount() {
        const urlString = "https://api.themoviedb.org/3/search/movie?query=marvel&api_key=ab0daf48e5c9f6b2513a909b48005ede&language=en-US&page=1&include_adult=false";

        axios.get(urlString)
            .then(function (response) {
                // handle success
                console.log("fetched data successfully");
                //console.log(response);

                const results = response.data.results;
                //console.log(results[0])

                let movieRows = []; 

                results.forEach((movie) => {
                    console.log(movie.title)
                    const movieRow = <MovieRow movie={movie} />
                    movieRows.push(movie)
                })

                this.setState({rows: movieRows})
            })
            .catch(function (error) {
                // handle error
                console.log("fetched data UNsuccessfully");
                console.log(error);
            })
            .finally(function () {
                // always executed
            });

    }

    render() {
        return (
            <div>
                {this.state.rows}
            </div>
        );
    }

}
